
public class If01P92 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//conditionals: if ~ else
		
		//if(condition) {
		//  expression1;
		//  expression2;
		//  ............
		//}
		//Code nested inside the if function is executed
		//If false the function is skipped
		
		int a = 3;
		
		if(a < 4) {
			//outside variables can be used here
			System.out.println("a is smaller than 4");
		}
		//code afterwards not inside the if function is executed normally
		System.out.println("Executed normally");
		

	}

}
