
public class IfElse01P95 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//if-else statements execute code
		//both when the expression is true or false
		//if and else can be expressed separately but
		//combining them into one if-else statement saves resources
		//if true, else x
		
		int a = 15;
		
		if(a == 15) {
			System.out.println("a is 15");
		} else {
			System.out.println("a is not 15");
		}
	}

}
