package final1;

public class FinalMain01P278 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final int a = 50;
		System.out.println(a);
		//a = 500;
		//final variable: the variable can only be called and not modified
		//the final keyword can also be used for methods
		final int b;
		b=60;
		System.out.println(b);
		//works only because b is blank. once assigned a value it is final
	}

}
