package arrays1;

import java.util.Arrays;

public class Arrays05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//sorts can also work for strings
		String[] arr = {"apple","banana","Apple","giraffe","metro","Zetor","호하하하"};
		Arrays.sort(arr);
		System.out.println(Arrays.toString(arr));
		//sorts in Uppercase, lowercase, misc UTF-8 chars in order of ####

	}

}
