package math;

public class Math01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//math methods can be used without importing
		
		//absolute value
		int a = -1024;
		System.out.println(Math.abs(a));
		
		//ceiling round up
		double b = 3.14;
		System.out.println(Math.ceil(b));
		
		//floor round down
		double c = 19.9;
		System.out.println(Math.floor(c));

	}

}
